mapper.select:
SELECT 

FROM product_specification_customer_price a



		AND a.id = #{id}


		AND a.customer_id = #{customerId}


		AND a.product_specification_id = #{productSpecificationId}

SELECT , c.name AS "customerName", c.customer_no AS "customerNo", c.mt_city_cd AS "mtCityCd", cg.name AS "customerGroupName" FROM product_specification_customer_price a LEFT JOIN customer c ON c.id = a.customer_id LEFT JOIN supplier_customer sc ON sc.customer_id = c.id LEFT JOIN customer_group cg ON cg.id = sc.customer_group_id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.customer_id = #{customerId}
AND a.product_specification_id = #{productSpecificationId}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM product_specification_customer_price a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.customer_id = #{customerId}


		AND a.product_specification_id = #{productSpecificationId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE product_specification_customer_price SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
