mapper.select:
SELECT 

FROM specification a

WHERE a.id = #{id}

SELECT 

FROM specification a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.supplier_id = #{supplierId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM specification a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.supplier_id = #{supplierId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE specification SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
